class ProductBlock extends HTMLElement{constructor(){super()}connectedCallback(){this.render()}render(){const t=this.getAttribute("data-product-id"),e=this.getAttribute("image-src"),c=this.getAttribute("title"),n=this.getAttribute("price");this.innerHTML=`\n        <img src="${e}">\n        <div class="block__panel">\n          <h3 class="block__title">${c}</h3>\n          <button data-product-id="${t}" class="btn">Add to cart - ${n}</button>\n        </div>\n      `}}window.customElements.define("product-block",ProductBlock);